select

  distinct


from dt_user

  


  order by ${orderByClause}


  
    limit ${offset}, ${limit}
  
  
    limit ${limit}

  select id

  from dt_user 
  where
  
  country = #{country,jdbcType=VARCHAR}
  
  
   and city = #{city,jdbcType=VARCHAR}
  
  
  and area = #{area,jdbcType=VARCHAR}
  
  
  and spare = #{spare,jdbcType=VARCHAR}
  
  and id != #{id,jdbcType=INTEGER}

 select 

 from dt_user 

 
 country = #{country,jdbcType=VARCHAR}
 
 
  and city = #{city,jdbcType=VARCHAR}
 
 
 and area = #{area,jdbcType=VARCHAR}
 
 
 and spare = #{spare,jdbcType=VARCHAR}
 
 and id != #{id,jdbcType=INTEGER}

  select id

  from (select * from dt_user ORDER BY num desc LIMIT #{paihang,jdbcType=INTEGER} ) t 
 ORDER BY RAND() LIMIT #{getnum,jdbcType=INTEGER};

  select id

  from (select * from dt_user where country = #{country,jdbcType=VARCHAR} and city!= #{city,jdbcType=VARCHAR}) t 
 ORDER BY RAND() LIMIT #{quenum,jdbcType=INTEGER};
